import React, { useState } from 'react';
import { BrowserRouter, Navigate, Route, Routes } from 'react-router-dom';
import './App.css';
import Layout from './components/Layout';
import PrivateRoute from './components/PrivateRoute';
import Home from './pages/Home';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Stocks from './pages/Stocks';

const App = () => {
  const [user, setUser] = useState(null); // Track if user is authenticated

  return (
    <BrowserRouter>
      <Routes>
        <Route element={<Layout />}>
          {/* Redirect root to login if user is not authenticated */}
          <Route path="/" element={user ? <Navigate to="/stocks/ABC" /> : <Navigate to="/login" />} />

          {/* Public Routes */}
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login setUser={setUser} />} />

          {/* Protected Route for Stocks */}
          <Route 
            path="/stocks/:symbols" 
            element={<PrivateRoute element={<Stocks />} user={user} />} 
          />

          {/* Home route example */}
          <Route path="/home" element={<Home />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
};

export default App;
