import axios from 'axios';
import React, { useEffect, useState } from 'react';

const Slider = () => {
    const [data, setData] = useState(null);  // Data state
    const [loading, setLoading] = useState(true);  // Loading state
    const [error, setError] = useState(null);  // Error state
    const apiKey = process.env.REACT_APP_API_KEY;  // API key from environment

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);  // Start loading
            setError(null);  // Reset any previous errors

            try {
                const options = {
                    method: 'GET',
                    url: 'https://yfapi.net/v6/finance/quote/marketSummary',
                    headers: {
                        'x-api-key': apiKey
                    }
                };

                const response = await axios.request(options);
                setData(response.data.marketSummaryResponse.result);  // Set the fetched data
                setLoading(false);  // Stop loading after data is fetched
            } catch (error) {
                console.error(error);
                setError('Error loading data. Please try again later.');  // Set error message
                setLoading(false);  // Stop loading on error
            }
        };

        fetchData();
    }, [apiKey]);  // Dependency on apiKey, but should only change if it changes in env

    if (loading) {
        return <div>Loading...</div>;  // Show loading text
    }

    if (error) {
        return <div>{error}</div>;  // Show error message if there's an issue
    }

    return (
        <div className='slider'>
            <div>
                {data && data.map((item, index) => (
                    <span className='slider-market-raw' key={index}>
                        <span className='slider-name'>{item.shortName}</span>  
                        {" "}
                        {item.regularMarketPrice.fmt}
                        <span style={{ color: item.regularMarketChange.raw > 0 ? "green" : "red" }}>
                            {" "} {item.regularMarketChange.fmt} {" "} 
                            ({item.regularMarketChangePercent.fmt})
                        </span>
                    </span>
                ))}
            </div>
        </div>
    );
};

export default Slider;
