import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import LineChart from '../components/Chart';

const Stocks = () => {
    const [chartData, setChartData] = useState({});
    const [summary, setSummary] = useState(null);
    const [flag, setFlag] = useState(null);
    const { symbol } = useParams();  // Retrieve the stock symbol from the URL
    const apiKey = process.env.REACT_APP_API_KEY;

    // Log the stock symbol for debugging
    console.log("Stock Symbol:", symbol);

    useEffect(() => {
        const fetchTrend = async () => {
            try {
                if (!symbol) {
                    console.error('No stock symbol passed in URL.');
                    setFlag(false);  // If no symbol, set flag to false
                    return;
                }

                // Log the stock symbol before making API request
                console.log("Stock Symbol for API Request:", symbol);

                const options = {
                    method: 'GET',
                    url: `https://yfapi.net/v8/finance/chart/${symbol}?range=1mo&interval=1d`,
                    headers: {
                        'x-api-key': apiKey
                    }
                };

                const response = await axios.request(options);

                console.log("API Response:", response);  // Log full response for debugging

                // Check if chart data exists and if result is valid
                if (response.data && response.data.chart && response.data.chart.result) {
                    const result = response.data.chart.result[0];
                    console.log("Chart Data:", result);

                    if (result && result.timestamp && result.indicators && result.indicators.quote) {
                        // Prepare chart data
                        setChartData({
                            labels: result.timestamp.map((elem) =>
                                new Date(elem * 1000).toLocaleDateString("en-US")
                            ),
                            datasets: [{
                                label: 'Changes Reflected Daily',
                                data: result.indicators.quote[0].close,
                                backgroundColor: [
                                    'rgba(255, 99, 132, 0.2)',
                                    'rgba(54, 162, 235, 0.2)',
                                    'rgba(255, 206, 86, 0.2)',
                                    'rgba(75, 192, 192, 0.2)',
                                    'rgba(153, 102, 255, 0.2)',
                                    'rgba(255, 159, 64, 0.2)'
                                ],
                                borderWidth: 5
                            }],
                        });

                        setSummary(result.meta);
                        setFlag(true);  // Data loaded successfully, set flag to true
                    } else {
                        console.log('Missing expected data structure inside chart.result');
                        setFlag(false);  // If data is missing, set flag to false
                    }
                } else {
                    console.log('No valid chart data in the response', response.data.chart);
                    setFlag(false);  // If no chart data, set flag to false
                }
            } catch (error) {
                console.error('API error:', error);  // Log errors
                setFlag(false);  // Error occurred, set flag to false
            }
        };

        fetchTrend();
    }, [apiKey, symbol]);  // Dependencies: apiKey and symbol

    return (
        <>
            {
                !flag ? (
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <div className="card rounded shadow-lg m-2 chart-true">
                                    <p className="error-message" style={{ padding: "9px" }}>Loading Data...</p>
                                </div>
                            </div>
                        </div>
                    </div>
                ) : (
                    <div className="stock-row">
                        <div className="stock-column-data">
                            {
                                summary ? (
                                    <>
                                        <h2><span style={{ color: "black" }}><span className='summary-span'>{summary.exchangeName} ({summary.symbol})</span> ${summary.regularMarketPrice}</span></h2>
                                        <div className='stock-headings'>
                                            <h4><span className='summary-span'>Exchange Name:</span><span style={{ color: "black"}}> {" "} {summary.exchangeName}</span></h4>
                                            <hr /><br />
                                            <h4><span className='summary-span'>Instrument Type:</span><span style={{ color: "black"}}>{" "} {summary.instrumentType}</span></h4>
                                            <hr /><br />
                                            <h4><span className='summary-span'>Previous Close Price:</span><span style={{ color: "black"}}>{" "} ${summary.chartPreviousClose}</span></h4>
                                            <hr /><br />
                                            <h4><span className='summary-span'>Current Price:</span><span style={{ color: "black"}}>{" "} ${summary.regularMarketPrice}</span></h4>
                                            <hr /><br />
                                        </div>
                                    </>
                                ) : (
                                    <div>Loading....</div>
                                )
                            }
                        </div>
                        <div className="stock-column-chart">
                            <LineChart chartData={chartData} />
                        </div> 
                    </div>
                )
            }
        </>
    );
};

export default Stocks;
