import React from 'react';
import Header from '../components/Header';
import Slider from '../components/Slider';

const Layout = ({ children, user, onLogout }) => {
  return (
    <>
      <Slider />
      <Header />

      <main>
        {user && (
          <div>
            <h2>Welcome, {user}!</h2>
            <button onClick={onLogout}>Sign Out</button>
          </div>
        )}
        {children}
      </main>
    </>
  );
}

export default Layout;
