import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import LineChart from '../components/Chart';

const Stocks = () => {
    const [chartData, setChartData] = useState({});
    const [summary, setSummary] = useState(null);
    const [flag, setFlag] = useState(false);  // Set default to false, meaning loading
    const location = useLocation();
    const stockSymbol = location.state?.symbol;  // Retrieve stock symbol from state
    const apiKey = process.env.REACT_APP_API_KEY;

    useEffect(() => {
        console.log("Stock Symbol:", stockSymbol);  // Log the symbol received from state

        if (!stockSymbol) {
            console.log("No stock symbol passed in state.");
            return;  // If no stock symbol, return early
        }

        const fetchTrend = async () => {
            try {
                const options = {
                    method: 'GET',
                    url: `https://yfapi.net/v8/finance/chart/${stockSymbol}?range=1mo&interval=1d`,
                    headers: {
                        'x-api-key': apiKey
                    }
                };

                const response = await axios.request(options);

                // Log the response data to inspect the structure
                console.log("API Response:", response.data);

                if (response.data && response.data.chart && response.data.chart.result.length > 0) {
                    setChartData({
                        labels: response.data.chart.result[0].timestamp.map((elem) =>
                            new Date(elem * 1000).toLocaleDateString("en-US")
                        ),
                        datasets: [{
                            label: 'Changes Reflected Daily',
                            data: response.data.chart.result[0].indicators.quote[0].close,
                            backgroundColor: [
                                'rgba(255, 99, 132, 0.2)',
                                'rgba(54, 162, 235, 0.2)',
                                'rgba(255, 206, 86, 0.2)',
                                'rgba(75, 192, 192, 0.2)',
                                'rgba(153, 102, 255, 0.2)',
                                'rgba(255, 159, 64, 0.2)'
                            ],
                            borderWidth: 5
                        }],
                    });
                    setSummary(response.data.chart.result[0].meta);
                    setFlag(true);  // Set flag to true when data is successfully fetched
                } else {
                    console.log('No data found in the response');
                    setFlag(false);  // Set flag to false if no data found
                }
            } catch (error) {
                console.error('API error:', error);
                setFlag(false);  // Set flag to false in case of error
            }
        };

        fetchTrend();
    }, [apiKey, stockSymbol]);  // Dependency array includes apiKey and stockSymbol

    return (
        <>
            {flag ? (
                <div className="stock-row">
                    <div className="stock-column-data">
                        {summary ? (
                            <>
                                <h2>
                                    <span style={{ color: "black" }}>
                                        <span className='summary-span'>{summary.exchangeName} ({summary.symbol})</span> ${summary.regularMarketPrice}
                                    </span>
                                </h2>
                                <div className='stock-headings'>
                                    <h4><span className='summary-span'>Exchange Name:</span><span style={{ color: "black" }}> {summary.exchangeName}</span></h4>
                                    <hr /><br />
                                    <h4><span className='summary-span'>Instrument Type:</span><span style={{ color: "black" }}> {summary.instrumentType}</span></h4>
                                    <hr /><br />
                                    <h4><span className='summary-span'>Previous Close Price:</span><span style={{ color: "black" }}> ${summary.chartPreviousClose}</span></h4>
                                    <hr /><br />
                                    <h4><span className='summary-span'>Current Price:</span><span style={{ color: "black" }}> ${summary.regularMarketPrice}</span></h4>
                                    <hr /><br />
                                </div>
                            </>
                        ) : (
                            <div>Loading....</div>
                        )}
                    </div>
                    <div className="stock-column-chart">
                        <LineChart chartData={chartData} />
                    </div>
                </div>
            ) : (
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <div className="card rounded shadow-lg m-2 chart-true">
                                <p className="error-message" style={{ padding: "9px" }}>Loading Data...</p>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
};

export default Stocks;
