import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import LineChart from '../components/Chart';

const Stocks = () => {
    const [chartData, setChartData] = useState({});
    const [summary, setSummary] = useState(null);
    const [flag, setFlag] = useState(null);
    const { state } = useLocation();
    const apiKey = process.env.REACT_APP_API_KEY;

    useEffect(() => {
        const fetchTrend = async () => {
            try {
                const symbol = state; // Retrieve the stock symbol passed from the previous page

                if (!symbol) {
                    console.error('No stock symbol passed in state.');
                    setFlag(false); // No symbol means no data
                    return;
                }

                console.log("Stock Symbol:", symbol); // Debug: check if symbol is correct

                const options = {
                    method: 'GET',
                    url: `https://yfapi.net/v8/finance/chart/${symbol}?range=1mo&interval=1d`,
                    headers: {
                        'x-api-key': apiKey
                    }
                };

                const response = await axios.request(options);

                // Log the full API response for inspection
                console.log("API Response:", response);

                // Check if the response contains the expected data
                if (response.data && response.data.chart && response.data.chart.result && response.data.chart.result.length > 0) {
                    setChartData({
                        labels: response.data.chart.result[0].timestamp.map((elem) =>
                            new Date(elem * 1000).toLocaleDateString("en-US")
                        ),
                        datasets: [{
                            label: 'Changes Reflected Daily',
                            data: response.data.chart.result[0].indicators.quote[0].close,
                            backgroundColor: [
                                'rgba(255, 99, 132, 0.2)',
                                'rgba(54, 162, 235, 0.2)',
                                'rgba(255, 206, 86, 0.2)',
                                'rgba(75, 192, 192, 0.2)',
                                'rgba(153, 102, 255, 0.2)',
                                'rgba(255, 159, 64, 0.2)'
                            ],
                            borderWidth: 5
                        }],
                    });

                    setSummary(response.data.chart.result[0].meta);
                    setFlag(true); // Data loaded successfully, set flag to true
                } else {
                    console.log('No valid data in the response', response.data); // Log if the response is invalid
                    setFlag(false); // If no data is found, set flag to false
                }
            } catch (error) {
                console.error('API error:', error); // Log API errors
                setFlag(false); // Set flag to false in case of an error
            }
        };

        fetchTrend();
    }, [apiKey, state]); // Dependencies: apiKey and state (stock symbol)

    return (
        <>
            {
                !flag ? (
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <div className="card rounded shadow-lg m-2 chart-true">
                                    <p className="error-message" style={{ padding: "9px" }}>Loading Data...</p>
                                </div>
                            </div>
                        </div>
                    </div>
                ) : (
                    <div className="stock-row">
                        <div className="stock-column-data">
                            {
                                summary ? (
                                    <>
                                        <h2><span style={{ color: "black" }}><span className='summary-span'>{summary.exchangeName} ({summary.symbol})</span> ${summary.regularMarketPrice}</span></h2>
                                        <div className='stock-headings'>
                                            <h4><span className='summary-span'>Exchange Name:</span><span style={{ color: "black"}}> {" "} {summary.exchangeName}</span></h4>
                                            <hr /><br />
                                            <h4><span className='summary-span'>Instrument Type:</span><span style={{ color: "black"}}>{" "} {summary.instrumentType}</span></h4>
                                            <hr /><br />
                                            <h4><span className='summary-span'>Previous Close Price:</span><span style={{ color: "black"}}>{" "} ${summary.chartPreviousClose}</span></h4>
                                            <hr /><br />
                                            <h4><span className='summary-span'>Current Price:</span><span style={{ color: "black"}}>{" "} ${summary.regularMarketPrice}</span></h4>
                                            <hr /><br />
                                        </div>
                                    </>
                                ) : (
                                    <div>Loading....</div>
                                )
                            }
                        </div>
                        <div className="stock-column-chart">
                            <LineChart chartData={chartData} />
                        </div> 
                    </div>
                )
            }
        </>
    );
};

export default Stocks;
